svg.script:
        const max_cube = Math.max.apply(Math, ['P','E','T','E2','R','L','E3','E4'].map(x=>document.getElementById(x).children.length));
        const total_time = 0.3*(max_cube+3);

        let style = '';
        const text = ['P','E','T','E2','R','L','E3','E4'];
        for(let i=0;i<text.length;i++){
            const id = text[i];
            let group = Array.from(document.getElementById(id).children);
            if(text[i] == 'E3') group.reverse();
            let s = 0;

            group.forEach((u, index)=>{
                const animateId = `animate-${id}-${index}`;
                u.id = animateId + '-use';
                u.setAttribute("opacity","0");
                style += `.start #${u.id}{animation: ${total_time}s ${animateId} linear forwards;} `;
                s+=0.3;
                style += makeKeyFrame(animateId, index);
            });

        }
        document.getElementById('svgcss').appendChild(document.createTextNode(style));

        function makeKeyFrame(id, index){
            let keyframe = 
`@keyframes ${id} {
    0% {opacity: 0;}
    ${(0.3*index*100/total_time).toFixed(2)}% {opacity: 0;}
    ${(0.3*(index+1)*100/total_time).toFixed(2)}% {opacity: 1;}
    100%   {opacity: 1;}
}`;
            return keyframe;
        }
